export class ProductLayoutComponent {
  constructor(ecomService, compService, shared, route) {
    this.ecomService = ecomService;
    this.compService = compService;
    this.shared = shared;
    this.route = route;
    this.p = 1;
    this.emptyArray = false;
  }

  onProduct(i) {
    this.shared.clickedProduct = i;
    localStorage.setItem('clickedProduct', JSON.stringify(this.productsArray[i]));
    this.route.navigate(['/product', `${this.productsArray[i].product_id}`]);
  }

  onFashionDelete(i) {
    console.log(this.subCategory);
    this.subCategory.splice(i, 1);
    this.compService.fashionCategory.next(this.subCategory); // this.compService.checkValue.next(false)
  }

  onBrandDelete(i) {
    console.log(this.brandCategory[i]);
    this.brandCategory.splice(i, 1);
    this.compService.brandsCategory.next(this.brandCategory);
  }

  ngOnChanges() {
    if (this.productsArray == '') {
      this.emptyArray = true;
    } else {
      this.emptyArray = false;
    }
  }

  searchFilter(searchValue) {
    let filterData = this.productsArray.filter(data => {
      return data.brand.toLowerCase().includes(searchValue);
    });
    this.productsArray = filterData;
  }

  onSort(order) {
    let sorted;

    if (order == 1) {
      sorted = this.productsArray.sort((a, b) => {
        return a.price - b.price;
      });
    } else if (order == 0) {
      sorted = this.productsArray.sort((a, b) => {
        return b.price - a.price;
      });
    }

    this.productsArray = sorted;
  }

  ngOnInit() {
    this.compService.fashionCategory.subscribe(cat => {
      this.subCategory = cat;
      console.log(this.subCategory);
    });
    this.compService.brandsCategory.subscribe(cat => {
      this.brandCategory = cat;
    });
    this.ecomService.category.subscribe(res => {
      this.p = 1;
      console.log(res);
      this.productCategory = res;
      console.log(this.productCategory);
      let subCategory;

      if (this.productCategory) {
        console.log(this.productCategory);
        this.ecomService.categoryProducts(this.productCategory).subscribe({
          next: res => {
            this.p = 1;
            console.log(res);
            this.productsArray = res;
            this.searchProduct = res;
            this.displayedProductsArray = this.productsArray;
          }
        });
      }

      this.compService.fashionCategory.subscribe(cat => {
        this.fashionFilterChange(this.productCategory, cat);
        subCategory = cat;

        if (cat.length < 1) {
          this.ecomService.categoryProducts(this.productCategory).subscribe(res => {
            this.p = 1;
            this.productsArray = res;
          });
        }

        this.compService.brandsCategory.subscribe(brandcat => {
          this.brandChange(this.productCategory, cat, brandcat);

          if (brandcat.length < 1) {
            this.ecomService.categoryProducts(this.productCategory).subscribe(res => {
              this.p = 1;
              this.productsArray = res;
            });
            this.emptyArray = false;
          }
        });
      });
      this.compService.fashionCategory.subscribe(cat => {
        this.compService.priceRange.subscribe(res => {
          this.priceRange(res);

          if (res.length < 1 && this.subCategory.length > 0) {
            console.log(this.subCategory);
            this.fashionFilterChange(this.productCategory, cat);
          } else if (res.length < 1 && this.productCategory) {
            this.ecomService.categoryProducts(this.productCategory).subscribe(res => {
              this.p = 1;
              this.productsArray = res;
            });
          }
        });
      });
    });
    this.compService.searchValue.subscribe(res => {
      this.searchFilter(res);
    });
  }

  priceRange(range) {
    let pricRangeArray = [];

    for (let i = 0; i < range.length; i++) {
      switch (range[i]) {
        case '0-500':
          {
            this.displayedProductsArray.map(products => {
              if (products.price <= 500) {
                pricRangeArray.push(products);
              }
            });
          }
          break;

        case '500-1000':
          {
            this.displayedProductsArray.map(products => {
              if (products.price > 500 && products.price <= 1000) {
                pricRangeArray.push(products);
              }
            });
          }
          break;

        case '1000-2000':
          {
            this.displayedProductsArray.map(products => {
              if (products.price > 1000 && products.price <= 2000) {
                pricRangeArray.push(products);
              }
            });
          }
          break;

        case '2000-5000':
          {
            this.displayedProductsArray.map(products => {
              if (products.price > 2000 && products.price <= 5000) {
                pricRangeArray.push(products);
              }
            });
          }
          break;

        case '5000+':
          {
            this.displayedProductsArray.map(products => {
              if (products.price > 5000) {
                pricRangeArray.push(products);
              }
            });
          }
      }

      this.productsArray = pricRangeArray;
    }
  }

  brandChange(mainCategory, fashionCategory, brand) {
    let brands = [];
    this.productsArray = [];

    for (let i = 0; i < fashionCategory.length; i++) {
      brands = [];

      for (let j = 0; j < brand.length; j++) {
        this.ecomService.brandCategory(mainCategory, fashionCategory[i], brand[j]).subscribe(res => {
          this.p = 1;
          brands.push(...res);
          this.productsArray = brands;

          if (this.productsArray == '') {
            this.emptyArray = true;
          } else {
            this.emptyArray = false;
          }
        });
      }
    }
  }

  fashionFilterChange(mainCategory, subCategory) {
    let fashionFilter = [];

    for (let i = 0; i < subCategory.length; i++) {
      this.ecomService.fashionCategory(mainCategory, subCategory[i]).subscribe(res => {
        this.p = 1;
        fashionFilter.push(...res);
        console.log(fashionFilter);
      });
    }

    this.productsArray = fashionFilter;
  }

}

