export class SidebarComponent {
  constructor(compService, ecom) {
    this.compService = compService;
    this.ecom = ecom;
    this.SubCategory = [];
    this.price = Price;
    this.kidsWear = BoysWear;
    this.boysWear = this.kidsWear.slice(0, 2);
    this.girlsWear = this.kidsWear.slice(2);
    this.mensWear = MENS_WEAR;
    this.womensWears = WOMENS_WEAR;
    this.menBrands = MENS_BRANDS;
    this.showBrands = false;
    this.brandsCategory = [];
    this.priceRange = [];
  }

  onCheck(event, i) {
    if (event.target.checked == true) {
      this.brandsCategory = [];
      this.SubCategory.push(event.target.value);

      if (this.selectedCategory == 'men') {
        this.mensWear[i].enableCheckBox = true;
      } else if (this.category == 'women') {
        this.womensWears[i].enableCheckBox = true;
      } else if (this.category == 'kids') {
        this.kidsWear[i].enableChildren = true;
      }

      this.compService.fashionCategory.next(this.SubCategory);
    } else if (event.target.checked == false) {
      let popedIndex = this.SubCategory.indexOf(event.target.value); // if (this.category == 'men') {
      //   this.mensWear[i].enableChildren = false;
      // } else if (this.category == 'women') {
      //   this.womensWears[i].enableChildren = false;
      // } else if (this.category == 'kids') {
      //   this.kidsWear[i].enableChildren = false;
      // }

      this.SubCategory.splice(popedIndex, 1);
      this.compService.fashionCategory.next(this.SubCategory);
      this.brandsCategory = [];
    }
  }

  onBrand(event, i) {
    if (event.target.checked == true) {
      this.menBrands[i].enableCheckBox = true;
      this.brandsCategory.push(event.target.value);
      this.compService.brandsCategory.next(this.brandsCategory);
    } else if (event.target.checked == false) {
      let popedIndex = this.brandsCategory.indexOf(event.target.value);
      this.brandsCategory.splice(popedIndex, 1);
      this.compService.brandsCategory.next(this.brandsCategory);
    }
  }

  onPrice(event) {
    if (event.target.checked == true) {
      this.priceRange.push(event.target.value);
      this.compService.priceRange.next(this.priceRange);
    } else if (event.target.checked == false) {
      let popedIndex = this.priceRange.indexOf(event.target.value);
      this.priceRange.splice(popedIndex, 1);
      this.compService.priceRange.next(this.priceRange);
    }
  }

  formatLabel(value) {
    if (value >= 1000) {
      return Math.round(value / 1000);
    }

    return value;
  }

  filterUnCheck(catWear, res) {
    catWear.forEach(wear => {
      if (res.includes(wear.type)) {
        wear.enableCheckBox = true;
      } else {
        wear.enableCheckBox = false;
      }
    });
  }

  ngOnInit() {
    this.ecom.category.subscribe(catres => {
      this.selectedCategory = catres;

      if (catres == 'men' || 'women' || 'kids') {
        this.SubCategory = [];
      }

      this.compService.fashionCategory.subscribe(res => {
        if (catres == 'men') {
          this.filterUnCheck(this.mensWear, res);
        } else if (catres == 'women') {
          this.filterUnCheck(this.womensWears, res);
        } else {
          this.filterUnCheck(this.kidsWear, res);
        }
      });
      this.compService.brandsCategory.subscribe(res => {
        this.filterUnCheck(this.menBrands, res);
      }); // this.compService.category.subscribe((res: any) => {
      //   this.category = res;
      // });
      // if (res) {
      //   this.brandsCategory = [];
      // }
    });
  }

}

